package controlstmts;

public class Dowhileloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=1;i<=10;i++)
		{
			if(i==5)
			{
				//break;
				//exit from the loop
				continue;
				//skip from the condition and continue
			}
			System.out.println(i);
		}

	}

}
